BEGIN -- select COUNT(*)/2.0 INTO V_PenaltyDay from thr_em_attn_proc d where d.thr_empl_info_id=p_employee_ID and d.status='P' and attendance_date BETWEEN p_from_date AND p_TO_DATE and (in_time is null or out_time is null);
